const db = require('db');

module.exports = update;

async function update(phone_number, user, cb) {

  cb = cb || function() {};

  if (!user && !phone_number) {
    return cb({
      status: 400,
      err: new Error('low args')
    });
  }

  try {
    let r = await db.users.update({
      phone_number
    }, user);

    if (r.ok && r.n && r.nModified) {
      return true;
    }

    return false;

  } catch (err) {
    cb({
      err
    });

    return false;
  }
}
